svg.script:
if(typeof Array.prototype.map!=="function"){Array.prototype.map=function(a){for(var b=this,c=b.length,d=[],e=0,f;e<b;)d[e]=e in b?a.call(arguments[1],b[e],e++,b):f;return d}};
function scale(max, min, num) {return (100 * (num - min) / (max - min)) || 0;}
function draw() {
  var elem = document.getElementsByTagName("svg")[0],
      search = location.search.substr(1)
      segments = search.split('&'),
      parts = segments[0]
              .split(",")
              .map(function (n) {
                return parseFloat(n, 10) || 0;
              }),

      color = (search.indexOf('color') != -1),
      max = Math.max.apply(null, parts),
      min = Math.min.apply(null, parts);

  if ( color ) {
    for (var i=0; i < segments.length; i++) {
      if (segments[i].indexOf('color') != -1) {
        var color = "rgba("+segments[i].replace('color','')+")";
      }
    }
  } else {
    var color = "rgba(0,0,0,0.5)";
  }

  parts = parts.map(function (num) { return scale(max, min, num); });
  var x, y, height, width = 100 / parts.length;
  for (var i=0; i < parts.length; i++) {
    var rect = document.createElementNS("http://www.w3.org/2000/svg", "rect");
    x = 100 * (i / parts.length);
    y = 100 - parts[i];
    height = parts[i] + 1;
    rect.setAttribute("x", x + "%");
    rect.setAttribute("y", y + "%");
    rect.setAttribute("width", width + "%");
    rect.setAttribute("height", height + "%");
    rect.setAttribute("fill", color);
    rect.setAttribute("stroke", color);
    elem.appendChild(rect);
  }
}
