svg.script:
if(typeof Array.prototype.map!=="function"){Array.prototype.map=function(a){for(var b=this,c=b.length,d=[],e=0,f;e<b;)d[e]=e in b?a.call(arguments[1],b[e],e++,b):f;return d}};
function scale(max, min, num) {return (100 * (num - min) / (max - min)) || 0;}
function draw() {
  var elem = document.getElementsByTagName("svg")[0],
      search = location.search.substr(1),
      segments = search.split('&'),
      parts = segments[0]
              .split(",")
              .map(function (n) {
                return parseFloat(n, 10) || 0;
              }),
      current = (search.indexOf('current') != -1),
      max = Math.max.apply(null, parts),
      min = Math.min.apply(null, parts);
  parts = parts.map(function (num) { return scale(max, min, num); });
  var x1 = 0, y1 = 0, x2 = 0, y2 = 100 - parts[0], div = 100 / parts.length;
  for (var i=0; i < parts.length; i++) {
    var ln = document.createElementNS("http://www.w3.org/2000/svg", "line");
    x1 = x2; y1 = y2;
    x2 = 100 * (i / parts.length) + (i * div / parts.length);
    y2 = 100 - parts[i];
    ln.setAttribute("x1", x1 + "%");
    ln.setAttribute("x2", x2 + "%");
    ln.setAttribute("y1", y1 + "%");
    ln.setAttribute("y2", y2 + "%");
    if( current && i+1 === parts.length ) {
      ln.setAttribute("stroke", "rgba(255,0,0,0.5)");
    } else {
      ln.setAttribute("stroke", "rgba(0,0,0,0.5)");
    }
    ln.setAttribute("stroke-width", "1");
    elem.appendChild(ln);
  }
}
